import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';

const Modal = ({ isOpen, onClose, onCadastro }) => {
  const [aluno, setAluno] = useState({
    nome: '',
    dataNascimento: '',
    telefone: '',
    dataMatricula: '',
    tipoAula: ''
  });
  const [mensagem, setMensagem] = useState('');
  const [erro, setErro] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAluno({ ...aluno, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMensagem('');
    setErro('');

    // Simulação do envio dos dados do aluno para o backend
    setTimeout(() => {
      try {
        // Simulação do sucesso do cadastro
        setMensagem('Aluno cadastrado com sucesso!');
        // Limpar os campos do formulário após o cadastro
        setAluno({
          nome: '',
          dataNascimento: '',
          telefone: '',
          dataMatricula: '',
          tipoAula: ''
        });
        // Executar a função de cadastro fornecida pelo componente pai
        onCadastro();
      } catch (error) {
        console.error('Erro ao cadastrar aluno:', error);
        setErro('Erro ao cadastrar aluno. Por favor, tente novamente.');
      }
    }, 1000); // Simulação de atraso de 1 segundo
  };

  return (
    <div className={`modal ${isOpen ? 'is-active' : ''}`}>
      <div className="modal-background" onClick={onClose}></div>
      <div className="modal-content">
        <div className="box">
          <h2 className="title">Cadastrar Novo Aluno</h2>
          {mensagem && <div className="notification is-success">{mensagem}</div>}
          {erro && <div className="notification is-danger">{erro}</div>}
          <form onSubmit={handleSubmit}>
            {/* Campos de formulário para o cadastro de alunos */}
            <div className="field">
              <label className="label">Nome</label>
              <input className="input" type="text" name="nome" value={aluno.nome} onChange={handleChange} required />
            </div>
            {/* Outros campos de formulário para o cadastro de alunos */}
            <button type="submit" className="button is-primary">Cadastrar</button>
          </form>
        </div>
      </div>
      <button className="modal-close is-large" aria-label="close" onClick={onClose}></button>
    </div>
  );
};

const Alunos = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [alunos, setAlunos] = useState([]);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleCadastro = () => {
    // Atualizar a lista de alunos após o cadastro
    // Simulação da atualização da lista de alunos (comentada)
    setAlunos([...alunos, { id: alunos.length + 1, nome: 'Novo Aluno' }]);
    closeModal();
  };

  return (
    <div>
      {/* Botão para abrir o modal */}
      <button className="button is-primary" onClick={openModal}>
        <FontAwesomeIcon icon={faPlus} />
        <span> Novo Aluno</span>
      </button>
      {/* Modal para o cadastro de novos alunos */}
      <Modal isOpen={isModalOpen} onClose={closeModal} onCadastro={handleCadastro} />
      {/* Lista de alunos cadastrados */}
      <ul>
        {alunos.map(aluno => (
          <li key={aluno.id}>{aluno.nome}</li>
        ))}
      </ul>
    </div>
  );
};

export default Alunos;
