import React, { useState, useEffect } from 'react';
import { Route, Switch, Redirect } from "react-router-dom";
import { Routes } from "./routes";

// Pages
import Alunos from './pages/Alunos';
import Turmas from './pages/Turmas';
import Agendamentos from './pages/Agendamentos';
import Login from './pages/Login';

// Components
import Sidebar from './components/Sidebar';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Preloader from "./components/Preloader";

const RouteWithLoader = ({ component: Component, ...rest }) => {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setLoaded(true), 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <Route {...rest} render={props => (
      <>
        <Preloader show={loaded ? false : true} />
        <Component {...props} />
      </>
    )} />
  );
};

const RouteWithSidebar = ({ component: Component, ...rest }) => {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setLoaded(true), 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <Route {...rest} render={props => (
      <>
        <Preloader show={loaded ? false : true} />
        <Sidebar />
        <main className="content">
          <Navbar />
          <Component {...props} />
          <Footer />
        </main>
      </>
    )} />
  );
};

export default () => (
  <Switch>
    {/* pages */}
    <RouteWithSidebar exact path={Routes.Alunos.path} component={Alunos} />
    <RouteWithSidebar exact path={Routes.Turmas.path} component={Turmas} />
    <RouteWithSidebar exact path={Routes.Agendamentos.path} component={Agendamentos} />
    <RouteWithSidebar exact path={Routes.Login.path} component={Login} />

    <Redirect to={Routes.NotFound.path} />
  </Switch>
);

