import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faInfoCircle, faEdit, faEllipsisH, faSearch, faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import alunoscss from '../scss/aluno.scss';
import { Card, Table, Dropdown, Button, ButtonGroup, InputGroup, FormControl, Container, Row, Col, Modal } from '@themesberg/react-bootstrap';
import CadastrarAluno from './CadastrarAlunos';

const AlunosTable = () => {
  const [alunos, setAlunos] = useState([]);
  const [search, setSearch] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editModalShow, setEditModalShow] = useState(false);
  const [editAlunoData, setEditAlunoData] = useState(null);
  const [deleteModalShow, setDeleteModalShow] = useState(false);
  const [deleteAlunoId, setDeleteAlunoId] = useState(null);

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);
  const handleEditModalClose = () => setEditModalShow(false);
  const handleDeleteModalClose = () => setDeleteModalShow(false);

  useEffect(() => {
    axios.get('http://localhost:3000/api/alunos')
      .then(response => {
        setAlunos(response.data);
      })
      .catch(error => {
        console.error('Erro ao obter alunos:', error);
      });
  }, []);

  const handleEdit = (aluno) => {
    setEditAlunoData(aluno);
    setEditModalShow(true);
  }

  const handleDelete = (id) => {
    setDeleteAlunoId(id);
    setDeleteModalShow(true);
  }

  const handleConfirmDelete = () => {
    axios.delete(`http://localhost:3000/api/alunos/${deleteAlunoId}`)
      .then(response => {
        setAlunos(prevAlunos => prevAlunos.filter(aluno => aluno.id !== deleteAlunoId));
        setDeleteAlunoId(null);
        setDeleteModalShow(false);
      })
      .catch(error => {
        console.error('Erro ao excluir aluno:', error);
      });
  }

  const filteredAlunos = alunos.filter(aluno => 
    aluno.nome.toLowerCase().includes(search.toLowerCase())
  );

  const TableRow = (props) => {
    const { id, nome, idade, telefone, data_matricula, tipo_aula } = props;

    return (
      <tr key={id}>
        <td>{nome}</td>
        <td>{idade}</td>
        <td>{telefone}</td>
        <td>{data_matricula}</td>
        <td>{tipo_aula}</td>
        <td>
          <Dropdown as={ButtonGroup}>
            <Dropdown.Toggle as={Button} split variant="link" className="text-dark m-0 p-0">
              <span className="icon icon-sm">
                <FontAwesomeIcon icon={faEllipsisH} className="icon-dark" />
              </span>
            </Dropdown.Toggle>
            <Dropdown.Menu>
              <Dropdown.Item onClick={() => handleEdit(props)}>
                <FontAwesomeIcon icon={faEdit} className="me-2" /> Editar
              </Dropdown.Item>
              <Dropdown.Item onClick={() => handleDelete(id)} className="text-danger">
                <FontAwesomeIcon icon={faTrashAlt} className="me-2" /> Remover
              </Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </td>
      </tr>
    );
  };

  return (
    <Container className="my-3">
      <h2>Alunos</h2>
      <Row className="justify-content-between">
        <Col xs="auto">
          <InputGroup style={{ maxWidth: '300px' }}>
            <InputGroup.Text>
              <FontAwesomeIcon icon={faSearch} />
            </InputGroup.Text>
            <FormControl 
              placeholder="Pesquisar" 
              value={search} 
              onChange={e => setSearch(e.target.value)} 
            />
          </InputGroup>
        </Col>
        <Col xs="auto">
          <Button variant="outline-primary" size="sm" onClick={handleShowModal} className="ms-2">
            <FontAwesomeIcon icon={faPlus} />
          </Button>
        </Col>
      </Row>

      <CadastrarAluno show={showModal} handleClose={handleCloseModal} />

      <Card border="light" className="shadow-sm">
        <Table responsive className="table-centered table-nowrap rounded mb-0">
          <thead className="thead-light">
            <tr>
              <th>Nome</th>
              <th>Idade</th>
              <th>Telefone</th>
              <th>Data de Matrícula</th>
              <th>Tipo de Aula</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>

          {filteredAlunos.map(aluno => <TableRow key={aluno.id} {...aluno} />)}
          </tbody>
        </Table>
      </Card>

      <Modal show={editModalShow} onHide={handleEditModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Editar Aluno</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {editAlunoData && (
            <CadastrarAluno 
              editData={editAlunoData} 
              handleClose={handleEditModalClose} 
            />
          )}
        </Modal.Body>
      </Modal>

      <Modal show={deleteModalShow} onHide={handleDeleteModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Exclusão</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Tem certeza que deseja excluir este aluno?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleDeleteModalClose}>
            Cancelar
          </Button>
          <Button variant="danger" onClick={handleConfirmDelete}>
            Confirmar Exclusão
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default AlunosTable;
