import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faInfoCircle } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import alunoscss from '../scss/aluno';

const Alunos = () => {
  const [alunos, setAlunos] = useState([]);

  useEffect(() => {
    // Aqui você pode fazer a requisição para obter os dados dos alunos do seu backend
    // Exemplo de requisição com axios:
    axios.get('http://localhost:5000/api/alunos')
      .then(response => {
        setAlunos(response.data);
      })
      .catch(error => {
        console.error('Erro ao obter alunos:', error);
      });
  }, []);

  return (
    <div className="container">
      <div className="d-flex justify-content-end mb-3">
        {/* Button que aciona o modal de cadastro de aluno */}
        <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#modalCadastroAluno">
          Novo Aluno
        </button>
      </div>

      {/* Modal de cadastro de aluno */}
      <div className="modal fade" id="modalCadastroAluno" tabIndex="-1" role="dialog" aria-labelledby="modalCadastroAlunoLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="modalCadastroAlunoLabel">Cadastro de Novo Aluno</h5>
              <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            {/* Formulário de cadastro de aluno aqui */}
            <form>
              <div className="modal-body">
                {/* Campos do formulário */}
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-dismiss="modal">Fechar</button>
                <button type="submit" className="btn btn-primary">Cadastrar</button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Tabela de alunos */}
      <table className="table">
        {/* Cabeçalho da tabela */}
        <thead>
          <tr>
            <th>Nome</th>
            <th>Idade</th>
            <th>Telefone</th>
            <th>Data de Matrícula</th>
            <th>Tipo de Aula</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {/* Renderização da lista de alunos */}
          {alunos.map(aluno => (
            <tr key={aluno.id}>
              <td>{aluno.nome}</td>
              <td>{aluno.idade}</td>
              <td>{aluno.telefone}</td>
              <td>{aluno.data_matricula}</td>
              <td>{aluno.tipo_aula}</td>
              <td>
                {/* Botões de ação para cada aluno */}
                <button className="btn btn-danger me-2">
                  <FontAwesomeIcon icon={faTrashAlt} />
                </button>
                <button className="btn btn-info">
                  <FontAwesomeIcon icon={faInfoCircle} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Alunos;
