import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faInfoCircle } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const Alunos = () => {
  const [alunos, setAlunos] = useState([]);

  useEffect(() => {
    // Aqui você pode fazer a requisição para obter os dados dos alunos do seu backend
    // Exemplo de requisição com axios:
    axios.get('http://localhost:5000/api/alunos')
      .then(response => {
        setAlunos(response.data);
      })
      .catch(error => {
        console.error('Erro ao obter alunos:', error);
      });
  }, []);

  return (
    <div className="container">
      <div className="d-flex justify-content-end mb-3">
        <button className="btn btn-primary">Novo Aluno</button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Idade</th>
            <th>Telefone</th>
            <th>Data de Matrícula</th>
            <th>Tipo de Aula</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {alunos.map(aluno => (
            <tr key={aluno.id}>
              <td>{aluno.nome}</td>
              <td>{aluno.idade}</td>
              <td>{aluno.telefone}</td>
              <td>{aluno.data_matricula}</td>
              <td>{aluno.tipo_aula}</td>
              <td>
                <button className="btn btn-danger me-2">
                  <FontAwesomeIcon icon={faTrashAlt} />
                </button>
                <button className="btn btn-info">
                  <FontAwesomeIcon icon={faInfoCircle} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Alunos;
