import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faInfoCircle, faEdit, faEllipsisH, faSearch, faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import { Card, Table, Dropdown, Button, ButtonGroup, InputGroup, FormControl, Container, Row, Col } from '@themesberg/react-bootstrap';
import CadastrarAluno from './CadastrarAlunos';

const AlunosTable = () => {
  const [alunos, setAlunos] = useState([]);
  const [search, setSearch] = useState('');
  const [showModal, setShowModal] = useState(false);

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  useEffect(() => {
    axios.get('http://localhost:3000/api/alunos')
      .then(response => {
        setAlunos(response.data);
      })
      .catch(error => {
        console.error('Erro ao obter alunos:', error);
      });
  }, []);

  const handleCadastro = (aluno) => {
    axios.post('http://localhost:3000/api/alunos', aluno)
      .then(response => {
        setAlunos(prevAlunos => [...prevAlunos, response.data]);
        handleCloseModal();
      })
      .catch(error => {
        console.error('Erro ao cadastrar aluno:', error);
      });
  };

  const handleRemover = (id) => {
    axios.delete(`http://localhost:3000/api/alunos/${id}`)
      .then(response => {
        setAlunos(prevAlunos => prevAlunos.filter(aluno => aluno.id !== id));
      })
      .catch(error => {
        console.error('Erro ao remover aluno:', error);
      });
  };

  const filteredAlunos = alunos.filter(aluno => 
    aluno.nome.toLowerCase().includes(search.toLowerCase())
  );

  const TableRow = ({ id, nome, idade, telefone, data_matricula, tipo_aula }) => {
    return (
      <tr key={id}>
        <td>{nome}</td>
        <td>{idade}</td>
        <td>{telefone}</td>
        <td>{data_matricula}</td>
        <td>{tipo_aula}</td>
        <td>
          <Dropdown as={ButtonGroup}>
            <Dropdown.Toggle as={Button} split variant="link" className="text-dark m-0 p-0">
              <span className="icon icon-sm">
                <FontAwesomeIcon icon={faEllipsisH} className="icon-dark" />
              </span>
            </Dropdown.Toggle>
            <Dropdown.Menu>
              <Dropdown.Item>
                <FontAwesomeIcon icon={faInfoCircle} className="me-2" /> Visualizar
              </Dropdown.Item>
              <Dropdown.Item>
                <FontAwesomeIcon icon={faEdit} className="me-2" /> Editar
              </Dropdown.Item>
              <Dropdown.Item className="text-danger" onClick={() => handleRemover(id)}>
                <FontAwesomeIcon icon={faTrashAlt} className="me-2" /> Remover
              </Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </td>
      </tr>
    );
  };

  return (
    <Container className="my-3">
      <h2>Alunos</h2>
      <Row className="justify-content-between">
        <Col xs="auto">
          <InputGroup style={{ maxWidth: '300px' }}>
            <InputGroup.Text>
              <FontAwesomeIcon icon={faSearch} />
            </InputGroup.Text>
            <FormControl 
              placeholder="Pesquisar" 
              value={search} 
              onChange={e => setSearch(e.target.value)} 
            />
          </InputGroup>
        </Col>
        <Col xs="auto">
          <Button variant="outline-primary" size="sm" onClick={handleShowModal} className="ms-2">
            <FontAwesomeIcon icon={faPlus} />
          </Button>
        </Col>
      </Row>

      <CadastrarAluno show={showModal} handleClose={handleCloseModal} handleCadastro={handleCadastro} />

      <Card border="light" className="shadow-sm">
        <Table responsive className="table-centered table-nowrap rounded mb-0">
          <thead className="thead-light">
            <tr>
              <th>Nome</th>
              <th>Idade</th>
              <th>Telefone</th>
              <th>Data de Matrícula</th>
              <th>Tipo de Aula</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {filteredAlunos.map(aluno => <TableRow key={aluno.id} {...aluno} />)}
          </tbody>
        </Table>
      </Card>
    </Container>
  );
};

export default AlunosTable;
