import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faInfoCircle, faEdit, faEllipsisH, faSearch, faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import { Card, Table, Dropdown, Button, ButtonGroup, InputGroup, FormControl, Container, Row, Col } from '@themesberg/react-bootstrap';
import CadastrarAluno from './CadastrarAlunos';

const AlunosTable = () => {
  const [alunos, setAlunos] = useState([
    { id: 1, nome: 'Maria Sofia', idade: 20, telefone: '(83)91234-5678', data_matricula: '2023-01-15', tipo_aula: 'Turma' },
    { id: 2, nome: 'Ana Clara', idade: 21, telefone: '(83)98765-4321', data_matricula: '2023-01-16', tipo_aula: 'Individual' },
    { id: 3, nome: 'Beatriz Oliveira', idade: 22, telefone: '(83)91234-6789', data_matricula: '2023-01-17', tipo_aula: 'Turma' },
    { id: 4, nome: 'Clara Martins', idade: 23, telefone: '(83)97654-3210', data_matricula: '2023-01-18', tipo_aula: 'Individual' },
    { id: 5, nome: 'Diana Silva', idade: 24, telefone: '(83)91234-8901', data_matricula: '2023-01-19', tipo_aula: 'Turma' },
    { id: 6, nome: 'Eloá Fernandes', idade: 25, telefone: '(83)94567-1234', data_matricula: '2023-01-20', tipo_aula: 'Individual' },
    { id: 7, nome: 'Fernanda Almeida', idade: 26, telefone: '(83)95678-2345', data_matricula: '2023-01-21', tipo_aula: 'Turma' },
    { id: 8, nome: 'Gabriela Costa', idade: 27, telefone: '(83)96789-3456', data_matricula: '2023-01-22', tipo_aula: 'Individual' },
    { id: 9, nome: 'Helena Souza', idade: 28, telefone: '(83)97890-4567', data_matricula: '2023-01-23', tipo_aula: 'Turma' },
    { id: 10, nome: 'Isabela Lima', idade: 29, telefone: '(83)98901-5678', data_matricula: '2023-01-24', tipo_aula: 'Individual' }
  ]);
  const [search, setSearch] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [alunoEditando, setAlunoEditando] = useState(null);

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => {
    setShowModal(false);
    setAlunoEditando(null);
  };

  useEffect(() => {
    obterAlunos();
  }, []);

  const obterAlunos = () => {
    // Simulando uma requisição ao backend para obter os alunos
    // Aqui você faria a requisição real ao seu backend
    // Exemplo: axios.get('http://localhost:3000/api/alunos')
    // Em seguida, atualize o estado com os dados obtidos
  };

  const handleCadastro = (novoAluno) => {
    // Simulando o cadastro de um novo aluno
    // Aqui você faria a requisição real ao seu backend para cadastrar o novo aluno
    // Exemplo: axios.post('http://localhost:3000/api/alunos', novoAluno)
    // Após cadastrar o aluno, atualize o estado com os novos dados
    setAlunos([...alunos, { ...novoAluno, id: alunos.length + 1 }]);
    handleCloseModal();
  };

  const handleRemover = (id) => {
    // Simulando a remoção de um aluno
    // Aqui você faria a requisição real ao seu backend para remover o aluno com o ID fornecido
    // Exemplo: axios.delete(`http://localhost:3000/api/alunos/${id}`)
    // Após remover o aluno, atualize o estado sem o aluno removido
    setAlunos(alunos.filter(aluno => aluno.id !== id));
  };

  const handleEditar = (aluno) => {
    // Simulando a edição dos dados de um aluno
    // Aqui você faria a requisição real ao seu backend para editar os dados do aluno
    // Exemplo: axios.put(`http://localhost:3000/api/alunos/${aluno.id}`, aluno)
    // Após editar o aluno, atualize o estado com os novos dados
    setAlunos(alunos.map(a => (a.id === aluno.id ? aluno : a)));
    handleCloseModal();
  };

  const handleEditarClick = (aluno) => {
    setAlunoEditando(aluno);
    setShowModal(true);
  };

  const filteredAlunos = alunos.filter(aluno => 
    aluno.nome.toLowerCase().includes(search.toLowerCase())
  );

  const TableRow = ({ id, nome, idade, telefone, data_matricula, tipo_aula }) => {
    return (
      <tr key={id}>
        <td>{nome}</td>
        <td>{idade}</td>
        <td>{telefone}</td>
        <td>{data_matricula}</td>
        <td>{tipo_aula}</td>
        <td>
          <Dropdown as={ButtonGroup}>
            <Dropdown.Toggle as={Button} split variant="link" className="text-dark m-0 p-0">
              <span
className="icon icon-sm">
<FontAwesomeIcon icon={faEllipsisH} className="icon-dark" />
</span>
</Dropdown.Toggle>
<Dropdown.Menu>
<Dropdown.Item onClick={() => handleEditarClick({ id, nome, idade, telefone, data_matricula, tipo_aula })}>
<FontAwesomeIcon icon={faEdit} className="me-2" /> Editar
</Dropdown.Item>
<Dropdown.Item onClick={() => handleRemover(id)} className="text-danger">
<FontAwesomeIcon icon={faTrashAlt} className="me-2" /> Remover
</Dropdown.Item>
</Dropdown.Menu>
</Dropdown>
</td>
</tr>
);
};

return (
<Container className="my-3">
<h2>Alunos</h2>
<Row className="justify-content-between">
<Col xs="auto">
<InputGroup style={{ maxWidth: '300px' }}>
<InputGroup.Text>
<FontAwesomeIcon icon={faSearch} />
</InputGroup.Text>
<FormControl
placeholder="Pesquisar"
value={search}
onChange={e => setSearch(e.target.value)}
/>
</InputGroup>
</Col>
<Col xs="auto">
<Button variant="outline-primary" size="sm" onClick={handleShowModal} className="ms-2">
<FontAwesomeIcon icon={faPlus} />
</Button>
</Col>
</Row>
<CadastrarAluno 
    show={showModal} 
    handleClose={handleCloseModal} 
    handleCadastro={handleCadastro} 
    alunoEditando={alunoEditando}
    handleEditar={handleEditar}
  />

  <Card border="light" className="shadow-sm">
    <Table responsive className="table-centered table-nowrap rounded mb-0">
      <thead className="thead-light">
        <tr>
          <th>Nome</th>
          <th>Idade</th>
          <th>Telefone</th>
          <th>Data de Matrícula</th>
          <th>Tipo de Aula</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        {filteredAlunos.map(aluno => <TableRow key={aluno.id} {...aluno} />)}
      </tbody>
    </Table>
  </Card>
</Container>
);
};

export default AlunosTable;
