import React, { useState } from "react";
import { Form, Button } from "@themesberg/react-bootstrap";

const CadastrarTreinos = ({ onClose, onAddTreino }) => {
  const [nome, setNome] = useState("");
  const [descricao, setDescricao] = useState("");
  const [serie, setSerie] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTreino = { id: Date.now(), nome, descricao, serie };
    onAddTreino(newTreino);
    onClose();
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="formNome">
        <Form.Label>Nome</Form.Label>
        <Form.Control
          type="text"
          placeholder="Nome do treino"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group controlId="formDescricao" className="mt-3">
        <Form.Label>Descrição</Form.Label>
        <Form.Control
          type="text"
          placeholder="Descrição do treino"
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group controlId="formSerie" className="mt-3">
        <Form.Label>Série</Form.Label>
        <Form.Control
          type="text"
          placeholder="Série do treino (ex: 3x10)"
          value={serie}
          onChange={(e) => setSerie(e.target.value)}
          required
        />
      </Form.Group>
      <Button variant="primary" type="submit" className="mt-3">
        Adicionar
      </Button>
    </Form>
  );
};

export default CadastrarTreinos;
